    private void StockTotalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_StockTotalActionPerformed
        txtNombre.setText("");
        CargarTablas(1, categoria, "");
    }//GEN-LAST:event_StockTotalActionPerformed

    private void txtNombreKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtNombreKeyReleased
        if (txtNombre.getText().equals("")){
            CargarTablas(1, categoria, "");
        }else{
            CargarTablas(2,categoria, txtNombre.getText());
        }
    }//GEN-LAST:event_txtNombreKeyReleased
    private void CargarTablas(int numero,String categoria, String nombre)
    {
        sql.Tabla_Inventario(numero, categoria,nombre);
    }
    private void CargarTablasFiltros(int numero ,String categoria){
        sql.Tabla_Inventario_Filtros(numero, categoria);
    }

